import React, { useEffect, useState } from 'react';
import AgentTable from './components/AgentTable';
import ExportButton from './components/ExportButton';
import { fetchAllAgents } from './api/agentApi';

function App() {
  const [agents, setAgents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState('');
  const [sortBy, setSortBy] = useState('deployed_at');
  const [sortOrder, setSortOrder] = useState('desc');

  const fetchData = () => {
    setLoading(true);
    fetchAllAgents({ search, sort_by: sortBy, sort_order: sortOrder })
      .then(data => {
        setAgents(data.active_agents.concat(data.archived_agents));
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching agents:', err);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchData();
  }, [search, sortBy, sortOrder]);

  return (
    <div style={{ padding: '20px' }}>
      <h1>AgentOps Dashboard</h1>

      {/* üîç Search Box */}
      <div style={{ marginBottom: '10px' }}>
        üîé <input
          type="text"
          placeholder="Search by agent name"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ padding: '5px', width: '250px' }}
        />
      </div>

      {/* üì§ Export Buttons */}
      <ExportButton />

      {/* üìã Agent Table */}
      {loading ? <p>Loading...</p> : (
        <AgentTable
          agents={agents}
          onSortChange={(col) => {
            setSortBy(col);
            setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
          }}
        />
      )}
    </div>
  );
}

export default App;
